CREATE TABLE IF NOT EXISTS `ims_tiny_wmall_account` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL DEFAULT '0', `uid` int(10) unsigned NOT NULL DEFAULT '0', `username` varchar(50) NOT NULL, `status` tinyint(3) NOT NULL DEFAULT '2', `store_nums` tinyint(3) unsigned NOT NULL DEFAULT '1', `store_ids` varchar(255) NOT NULL, `joindate` int(10) unsigned NOT NULL DEFAULT '0', `joinip` varchar(15) NOT NULL DEFAULT '', `lastvisit` int(10) unsigned NOT NULL DEFAULT '0', `lastip` varchar(15) NOT NULL DEFAULT '', `remark` varchar(500) NOT NULL DEFAULT '', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`), KEY `uid` (`uid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;
